}

  return (
    <div className="App">

       <h2> Date & Time picker</h2>
        <div>
          <label for='date'>Date</label>
          <input type='date' id='date'  value={selectedDate} onChange={event => setSelectedDate(event.target.value)} />
          <label for='time'>Time</label>
