import { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [selectedDate, setSelectedDate] = useState("");
  const [timeRemaining, setTimeRemaining] = useState({});
  const [intervalId, setIntervalId] = useState(null);
  const [countdownFinished, setCountdownFinished] = useState(false);

  const startCountdown = (event) => {
    event.preventDefault();
    if (selectedDate === "") {
      alert("Please select a valid target date and time.");
      return;
    }
    setCountdownFinished(false);

const validation = ()=>{
  if (selectedDate < new Date()) {
    alert("Please select a valid target date and time.");
    return;
  }
}



    const interval = setInterval(() => {
      const difference = new Date(selectedDate) - new Date();
      if (difference <= 0) {
        clearInterval(intervalId);
        setCountdownFinished(true);
      } else {
        const days = Math.floor(difference / (1000 * 60 * 60 * 24));
        const hours = Math.floor((difference / (1000 * 60 * 60)) % 24);
        const minutes = Math.floor((difference / 1000 / 60) % 60);
        const seconds = Math.floor((difference / 1000) % 60);
        setTimeRemaining({ days, hours, minutes, seconds });
      }
    }, 1000);
    setIntervalId(interval);
  };

  useEffect(() => {
    return () => {
      clearInterval(intervalId);
    };
  }, [intervalId]);

  const stopCountdown = () => {
    clearInterval(intervalId);
    setSelectedDate("");
    setTimeRemaining({});
  };

  return (
    <div className="App">
      <h1>Countdown Timer</h1>
      <h2> Date & Time picker</h2>
      <div>
        <input
          type="datetime-local"
          id="date"
          value={selectedDate}
          onChange={(event) => setSelectedDate(event.target.value)}
          style={{margin:'1rem'}}
        />
      </div>
      <button style={{margin:'0.5rem'}} onClick={startCountdown}>Submit</button>
      <button style={{margin:'0.5rem'}} onClick={stopCountdown}>Stop Countdown</button>
      <div>
        {timeRemaining.days !== undefined && !countdownFinished&&(
          <div style={{display:'flex', justifyContent:"center", flexDirection:'column'}}>
            <p className="">Time Remaining:</p>
          <div style={{display:'flex', justifyContent:"center"}}>

            <p className='timerBox' >{timeRemaining.days}<br/>days</p>
            <p className='timerBox'>{timeRemaining.hours}<br/>hours</p>
            <p className='timerBox'>{timeRemaining.minutes}<br/>minutes</p>
            <p className='timerBox'>{timeRemaining.seconds}<br/>seconds</p>
          </div>
          </div>
        )}
        {countdownFinished && (
          <div className="finished">
            <p>Countdown Finished!</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
