import { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [selectedDate, setSelectedDate] = useState("");
  const [timeRemaining, setTimeRemaining] = useState({});
  const [intervalId, setIntervalId] = useState(null);


  const startCountdown = (event) => {
    event.preventDefault(); 
    if (selectedDate === "") {
      alert("Please select a valid target date and time.");
      return;
    }

    const interval = setInterval(() => {
      const difference = new Date(selectedDate) - new Date();
      if (difference <= 0) {
        clearInterval(intervalId);
        alert("Countdown finished!");
      } else {
        const days = Math.floor(difference / (1000 * 60 * 60 * 24));
        const hours = Math.floor((difference / (1000 * 60 * 60)) % 24);
        const minutes = Math.floor((difference / 1000 / 60) % 60);
        const seconds = Math.floor((difference / 1000) % 60);
        setTimeRemaining({ days, hours, minutes, seconds });
      }
    }, 1000);
    setIntervalId(interval);
  };

  useEffect(() => {
    return () => {
      clearInterval(intervalId);
    };
  }, [intervalId]);

  const stopCountdown = () => {
    clearInterval(intervalId);
    setSelectedDate('');
    setTimeRemaining({});
  };


  return (
    <div className="App">
      <h1>Countdown Timer</h1>
      <h2> Date & Time picker</h2>
      <div>
        <label htmlFor="date">Date</label>
        <input
          type="datetime-local"
          id="date"
          value={selectedDate}
          onChange={(event) => setSelectedDate(event.target.value)}
        />
      </div>
      <button onClick={startCountdown}>Submit</button>
      <button onClick={stopCountdown}>Stop Countdown</button>
      <div>
        {timeRemaining.days !== undefined && (
          <div>
            <p>Time Remaining:</p>
            <p>{timeRemaining.days} days</p>
            <p>{timeRemaining.hours} hours</p>
            <p>{timeRemaining.minutes} minutes</p>
            <p>{timeRemaining.seconds} seconds</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
